import React, { useEffect, useState } from 'react';

const App = () => {
  const [hppData, setHppData] = useState(null);

  useEffect(() => {
    fetch('/hpp-init')
      .then(async (res) => {
        const text = await res.text();
        console.log('Raw response from /hpp-init:', text);

        try {
          const json = JSON.parse(text);
          setHppData(json);
        } catch (err) {
          console.error('❌ Failed to parse JSON from /hpp-init:', err);
        }
      })
      .catch((err) => {
        console.error('❌ Fetch error:', err);
      });
  }, []);

  if (!hppData) return <div>Loading...</div>;

  return (
    <div style={{ padding: '40px', textAlign: 'center' }}>
      <h1>Traditional Checkout</h1>
      <form
        method="POST"
        action={hppData.HPP_URL}
        style={{ marginTop: '20px' }}
      >
        <input type="hidden" name="MERCHANT_ID" value={hppData.MERCHANT_ID} />
        <input type="hidden" name="ACCOUNT" value={hppData.ACCOUNT} />
        <input type="hidden" name="ORDER_ID" value={hppData.ORDER_ID} />
        <input type="hidden" name="AMOUNT" value={hppData.AMOUNT} />
        <input type="hidden" name="CURRENCY" value={hppData.CURRENCY} />
        <input type="hidden" name="TIMESTAMP" value={hppData.TIMESTAMP} />
        <input type="hidden" name="SHA1HASH" value={hppData.SHA1HASH} />
        <input
          type="hidden"
          name="MERCHANT_RESPONSE_URL"
          value={hppData.MERCHANT_RESPONSE_URL}
        />
        <button type="submit">Pay Now</button>
      </form>
    </div>
  );
};

export default App;
