import React, { useEffect, useState } from 'react';

const Lightbox = () => {
  const [hppData, setHppData] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5001/hpp-init')
      .then((res) => res.json())
      .then((data) => setHppData(data))
      .catch((err) => console.error('Error loading HPP data:', err));
  }, []);

  const launchHPP = () => {
    document.getElementById('hppModal').classList.add('active');
    document.getElementById('hppForm').submit();
  };

  if (!hppData) {
    return <div style={{ padding: 20 }}>Loading checkout...</div>;
  }

  return (
    <div>
      <div style={{ maxWidth: '600px', margin: '40px auto', textAlign: 'center' }}>
        <h2>Lightbox Checkout</h2>
        <button onClick={launchHPP}>Launch HPP</button>
      </div>

      <div id="hppModal" style={{
        display: 'none',
        position: 'fixed',
        top: 0, left: 0,
        width: '100%',
        height: '100%',
        background: 'rgba(0,0,0,0.6)',
        justifyContent: 'center',
        alignItems: 'center',
        zIndex: 9999
      }} className="lightbox">
        <iframe
          name="hppIframe"
          id="hppIframe"
          title="HPP Frame"
          style={{
            width: '500px',
            height: '650px',
            border: 'none',
            borderRadius: '10px',
            background: 'white'
          }}
        ></iframe>
      </div>

      <form
        id="hppForm"
        method="POST"
        action={hppData.HPP_URL}
        target="hppIframe"
        style={{ display: 'none' }}
      >
        <input type="hidden" name="MERCHANT_ID" value={hppData.MERCHANT_ID} />
        <input type="hidden" name="ACCOUNT" value={hppData.ACCOUNT} />
        <input type="hidden" name="ORDER_ID" value={hppData.ORDER_ID} />
        <input type="hidden" name="AMOUNT" value={hppData.AMOUNT} />
        <input type="hidden" name="CURRENCY" value={hppData.CURRENCY} />
        <input type="hidden" name="TIMESTAMP" value={hppData.TIMESTAMP} />
        <input type="hidden" name="SHA1HASH" value={hppData.SHA1HASH} />
        <input type="hidden" name="MERCHANT_RESPONSE_URL" value={hppData.MERCHANT_RESPONSE_URL} />
        <input type="hidden" name="HPP_DISPLAY_MODE" value="iframe" />
      </form>
    </div>
  );
};

export default Lightbox;
