import React, { useEffect, useState } from 'react';

const App = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch(`/hpp-init?t=${Date.now()}`) // cache-buster
      .then((res) => res.json())
      .then((res) => {
        console.log("ðŸ§© HPP Payload:", res);
        setData(res);
      })
      .catch(console.error);
  }, []);

  if (!data) return <div>Loading checkout formâ€¦</div>;

  return (
    <div style={{ padding: '40px', fontFamily: 'Arial, sans-serif' }}>
      <h2>Traditional Checkout</h2>
      <p><strong>ORDER_ID:</strong> {data.ORDER_ID}</p>
      <p><strong>SHA1HASH:</strong> {data.SHA1HASH}</p>

      <form method="POST" action={data.HPP_URL}>
        <input type="hidden" name="MERCHANT_ID" value={data.MERCHANT_ID} />
        <input type="hidden" name="ACCOUNT" value={data.ACCOUNT} />
        <input type="hidden" name="ORDER_ID" value={data.ORDER_ID} />
        <input type="hidden" name="AMOUNT" value={data.AMOUNT} />
        <input type="hidden" name="CURRENCY" value={data.CURRENCY} />
        <input type="hidden" name="TIMESTAMP" value={data.TIMESTAMP} />
        <input type="hidden" name="SHA1HASH" value={data.SHA1HASH} />
        <input
          type="hidden"
          name="MERCHANT_RESPONSE_URL"
          value={data.MERCHANT_RESPONSE_URL}
        />
        <button type="submit" style={{ marginTop: '20px' }}>Pay Now</button>
      </form>
    </div>
  );
};

export default App;
